def getDecryptedString(encrypted, k):
    ret = []

    for char in encrypted:
        pos = ord(char) - ord("A")
        new_pos = (pos - (k % 26)) % 26
        ret.append(chr(new_pos + ord("A")))

    return "".join(ret)


